<?php	//echo 'p';	include('connectvars.php');	require_once('classes.php');	//echo 'kl';	$dbc=mysqli_connect(DB_HOST,DB_USER,DB_PASSWORD,DB_NAME);	if(isset($_POST['submit']))	{		//$detail=new array();		$username= mysqli_real_escape_string($dbc, trim($_POST['username']));		$password= mysqli_real_escape_string($dbc, trim($_POST['password']));		$fname= mysqli_real_escape_string($dbc, trim($_POST['fname']));		$detail=array(username=>$username,							password=>$password,							fname=>$fname);				$details=new userdetails($detail);		if( $details->dbinsert())				die('Successful.');		die('Unable to create account.');	}?><html>	<body>		<form name="frm" action="signup.php" method="post">			<input type="text" name="username" value="" />			<input type="password" name="password" value="" />			<input type="text" name="fname" value="" />			<input type="submit" name="submit" value="Submit" />		</form>	</body></html>